import { Card, Text, Image, Button, Group } from "@mantine/core";
import { IconShoppingCartPlus } from "@tabler/icons-react";
import img from "../assets/donwload.png";

const ProductCard = ({ img, name, price, discount }) => {
  return (
    <Card shadow="xl" mt="lg" withBorder radius="md" width={220}>
      <Card.Section
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          padding: "10px",
        }}
      >
        <Image
          height={240}
          width={200}
          src={img}
          fit="contain"
          radius="md"
          withPlaceholder
        />
      </Card.Section>

      <Text size="lg" weight={500} color="blue">
        {name}
      </Text>

      <Group position="apart">
        <Text size="md" weight={500}>
          {discount > 0 ? (
            <Text
              size="md"
              weight={500}
              color="red"
              style={{ textDecoration: "line-through" }}
            >
              ${price}
            </Text>
          ) : (
            ""
          )}
          ${price}$
        </Text>
        <Button variant="outline" size="xs" radius="xl">
          <IconShoppingCartPlus size={20} stroke={1.5} />
        </Button>
      </Group>
    </Card>
  );
};

export default ProductCard;
