import {
  TextInput,
  Textarea,
  SimpleGrid,
  Group,
  Title,
  Button,
  Container,
} from "@mantine/core";
import { useForm } from "@mantine/form";

const ContactPage = () => {
  const form = useForm({
    initialValues: {
      name: "",
      email: "",
      subject: "",
      message: "",
    },
    validate: {
      email: (value) => {
        if (!value) return "Email is required";
      },
    },
  });

  return (
    <Container size="xl">
      <form action="">
        <Title order={1} weight={900} align="center">
          Contact Us
        </Title>
        <SimpleGrid
          columns={1}
          spacing={0}
          breakpoints={[{ minWidth: "sm", cols: 1 }]}
        >
          <TextInput
            label="Name"
            placeholder="Enter your name"
            mt="md"
            {...form.getInputProps("name")}
          />
          <TextInput
            label="Email"
            placeholder="Enter your email"
            mt="md"
            {...form.getInputProps("email")}
          />
        </SimpleGrid>
        <TextInput
          label="Subject"
          placeholder="Enter your subject"
          mt="md"
          {...form.getInputProps("subject")}
        />
        <Textarea
          label="Message"
          placeholder="Enter your message"
          mt="md"
          {...form.getInputProps("message")}
        />
        <Group position="center" mt="xl">
          <Button type="submit" size="md">
            Send
          </Button>
        </Group>
      </form>
    </Container>
  );
};

export default ContactPage;
