import { useState } from "react";

import {
  Navbar,
  Space,
  Divider,
  Button,
  Avatar,
  FileButton,
  Title,
  Flex,
  Container,
  AppShell,
  SimpleGrid,
  TextInput,
  Tooltip,
  UnstyledButton,
} from "@mantine/core";
import { IconUser, IconKey, IconBell, IconLogout } from "@tabler/icons-react";
import Footer from "../components/Footer";
import Account from "./settings/Account";

const user = {
  firstName: "John",
  surName: "Doe",
  email: "admin@example.com",
  phone: "+1 234 567 890",
  avatar: "https://avatars.githubusercontent.com/u/25126241?v=4",
};

const Password = () => {};
const Notifications = () => {};

const data = [
  {
    label: "Account",
    icon: <IconUser stroke={1.5} />,
    element: <Account />,
  },
  {
    label: "Password",
    icon: <IconKey stroke={1.5} />,
    element: <Password />,
  },
  {
    label: "Notifications",
    icon: <IconBell stroke={1.5} />,
    element: <Notifications />,
  },
];

const labels = data.map((item) => {
  return { label: item.label, icon: item.icon };
});

const NavBar = ({ active, setActive }) => {
  return (
    <Navbar height={"100%"} p="xs" width={{ base: 80 }}>
      <Navbar.Section grow>
        {data.map((item, index) => {
          return (
            <Tooltip key={index} label={item.label} position="right">
              <Button
                variant={index === active ? "filled" : "subtle"}
                fullWidth
                onClick={() => {
                  setActive(index);
                }}
              >
                {item.icon}
              </Button>
            </Tooltip>
          );
        })}
      </Navbar.Section>

      <Navbar.Section>
        <Tooltip label="Logout" position="right">
          <Button variant="subtle" fullWidth>
            <IconLogout stroke={1.5} />
          </Button>
        </Tooltip>
      </Navbar.Section>
    </Navbar>
  );
};

const AccountPage = () => {
  const [active, setActive] = useState(0);

  return (
    <AppShell
      navbar={<NavBar active={active} setActive={setActive} />}
      footer={<Footer />}
    >
      <Account />
    </AppShell>
  );
};

export default AccountPage;
