import { useState } from "react";

import {
  Navbar,
  Space,
  Divider,
  Button,
  Avatar,
  Text,
  Container,
} from "@mantine/core";
import { IconUser, IconKey, IconBell, IconLogout } from "@tabler/icons-react";

import AccountNavbar from "../components/AccountNavbar";

const data = [
  {
    label: "Account",
    icon: <IconUser stroke={1.5} />,
    element: <Avatar size="lg" />,
  },
  {
    label: "Password",
    icon: <IconKey stroke={1.5} />,
    element: <Text>Change password</Text>,
  },
  {
    label: "Notifications",
    icon: <IconBell stroke={1.5} />,
    element: <Text>Change notifications</Text>,
  },
];

const labels = data.map((item) => {
  return { label: item.label, icon: item.icon };
});

const AccountPage = () => {
  const [active, setActive] = useState("Account");

  return (
    <Container size="md">
      <Navbar height={"90%"} width={{ sm: 300 }} p="md">
        <Navbar.Section>
          {data.map((item) => {
            return (
              <Button
                key={item.label}
                leftIcon={item.icon}
                variant={item.label === active ? "filled" : "subtle"}
                fullWidth
                onClick={() => {
                  setActive(item.label);
                }}
              >
                {item.label}
              </Button>
            );
          })}
        </Navbar.Section>

        <Space h="md" />
        <Divider />
        <Space h="md" />

        <Navbar.Section
          style={{
            bottom: 0,
          }}
        >
          <Button
            variant="subtle"
            leftIcon={<IconLogout stroke={1.5} />}
            fullWidth
          >
            Logout
          </Button>
        </Navbar.Section>
      </Navbar>
    </Container>
  );
};

export default AccountPage;
