import { useState } from "react";

import {
  Navbar,
  Space,
  Divider,
  Button,
  Avatar,
  Text,
  Title,
  Flex,
  Container,
  Group,
  AppShell,
  SimpleGrid,
  TextInput,
} from "@mantine/core";
import { IconUser, IconKey, IconBell, IconLogout } from "@tabler/icons-react";

import AccountNavbar from "../components/AccountNavbar";

const user = {
  firstName: "John",
  surName: "Doe",
  email: "admin@example.com",
  avatar: "https://avatars.githubusercontent.com/u/25126241?v=4",
};

const Account = () => {
  return (
    <>
      <Title>Edit Profile</Title>
      <SimpleGrid columns={1} bre>
        <TextInput label="First Name" value={user.firstName} />
        <TextInput label="Last Name" value={user.surName} />
      </SimpleGrid>
      <Avatar size={120} radius={120} />
    </>
  );
};
const Password = () => {};
const Notifications = () => {};

const data = [
  {
    label: "Account",
    icon: <IconUser stroke={1.5} />,
    element: <Account />,
  },
  {
    label: "Password",
    icon: <IconKey stroke={1.5} />,
    element: <Password />,
  },
  {
    label: "Notifications",
    icon: <IconBell stroke={1.5} />,
    element: <Notifications />,
  },
];

const labels = data.map((item) => {
  return { label: item.label, icon: item.icon };
});

const AccountPage = () => {
  const [active, setActive] = useState(0);

  return (
    <Flex>
      <Navbar height={"90vh"} p="xs" width={{ base: 300 }}>
        <Navbar.Section grow>
          {data.map((item, index) => {
            return (
              <Button
                key={index}
                leftIcon={item.icon}
                variant={index === active ? "filled" : "subtle"}
                fullWidth
                onClick={() => {
                  setActive(index);
                }}
              >
                {item.label}
              </Button>
            );
          })}
        </Navbar.Section>

        <Navbar.Section>
          <Button
            variant="subtle"
            leftIcon={<IconLogout stroke={1.5} />}
            fullWidth
          >
            Logout
          </Button>
        </Navbar.Section>
      </Navbar>
      <Account />
    </Flex>
  );
};

export default AccountPage;
