import {
  TextInput,
  PasswordInput,
  Paper,
  Title,
  Text,
  Container,
  Space,
  Button,
} from "@mantine/core";
import { useForm } from "@mantine/form";
import { Link } from "react-router-dom";
import { IconUser, IconUsers, IconAt, IconLock } from "@tabler/icons-react";

const RegisterPage = () => {
  const form = useForm({
    initialValues: {
      name: "",
      surname: "",
      email: "",
      password: "",
      confirmPassword: "",
    },
    validate: {
      name: (value) =>
        value.length < 2 ? "Name must be at least 2 characters" : null,
      surname: (value) =>
        value.length < 2 ? "Surname must be at least 2 characters" : null,
      email: (value) => (!/^\S+@\S+$/.test(value) ? "Invalid email" : null),
      password: (value, values) => {
        if (value.length < 2) {
          return "Password must be at least 2 characters";
        }
        if (value !== values.confirmPassword) {
          return "Passwords do not match";
        }
      },
      confirmPassword: (value, values) => {
        if (value.length < 2) {
          return "Password must be at least 2 characters";
        }
        if (value !== values.password) {
          return "Passwords do not match";
        }
      },
    },
  });

  return (
    <Container size={420} my={40}>
      <Title align="center">Create Account</Title>
      <Text color="dimmed" size="sm" align="center" mt={5}>
        Already have an account? <Link to={"/login"}>Login</Link>
      </Text>

      <Paper withBorder shadow="md" p={30} mt={30} radius="md">
        <form onSubmit={form.onSubmit(console.log)}>
          <TextInput
            label="Name"
            placeholder="Your Name"
            my="xs"
            icon={<IconUser stroke={1.5} size={"1rem"} />}
            {...form.getInputProps("name")}
          />
          <TextInput
            label="Surname"
            placeholder="Your Surname"
            my="xs"
            icon={<IconUsers stroke={1.5} size={"1rem"} />}
            {...form.getInputProps("surname")}
          />
          <TextInput
            label="Email"
            placeholder="Your Email"
            my="xs"
            icon={<IconAt stroke={1.5} size={"1rem"} />}
            {...form.getInputProps("email")}
          />
          <PasswordInput
            label="Password"
            placeholder="Your password"
            my="xs"
            icon={<IconLock stroke={1.5} size={"1rem"} />}
            {...form.getInputProps("password")}
          />
          <PasswordInput
            label="Confirm Password"
            placeholder="Confirm your password"
            my="xs"
            icon={<IconLock stroke={1.5} size={"1rem"} />}
            {...form.getInputProps("confirmPassword")}
          />
          <Space h="xl" />
          <Button type="submit" fullWidth mt="xl">
            Register
          </Button>
        </form>
      </Paper>
    </Container>
  );
};

export default RegisterPage;
