import { useState } from "react";

import {
  Navbar,
  Space,
  Divider,
  Button,
  Avatar,
  FileButton,
  Title,
  Flex,
  Container,
  AppShell,
  SimpleGrid,
  TextInput,
  Tooltip,
  UnstyledButton,
} from "@mantine/core";
import { IconUser, IconKey, IconBell, IconLogout } from "@tabler/icons-react";
import Footer from "../components/Footer";

const user = {
  firstName: "John",
  surName: "Doe",
  email: "admin@example.com",
  phone: "+1 234 567 890",
  avatar: "https://avatars.githubusercontent.com/u/25126241?v=4",
};

const Account = () => {
  return (
    <Container>
      <Flex justify="center" my="lg">
        <Title>Edit Profile</Title>
      </Flex>
      <Flex justify="center" my="xl">
        <FileButton>
          {(props) => (
            <UnstyledButton {...props}>
              <Avatar size={150} radius={150} src={user.avatar} />
            </UnstyledButton>
          )}
        </FileButton>
      </Flex>
      <SimpleGrid columns={1} breakpoints={[{ minWidth: "sm", cols: 2 }]}>
        <TextInput label="First Name" defaultValue={user.firstName} />
        <TextInput label="Last Name" defaultValue={user.surName} />
      </SimpleGrid>
      <TextInput label="Email" defaultValue={user.email} />
      <TextInput label="Phone" defaultValue={user.phone} />

      <Divider mt="xl" mb="xl" />

      <Flex justify="center" my="lg">
        <Title>Edit Adress</Title>
      </Flex>
      <TextInput
        label="Adress line 1"
        placeholder="Street name and house number"
      />
      <TextInput label="Adress line 2" placeholder="Apartment, unit etc." />
      <TextInput label="City" placeholder="City/Town" />
      <TextInput label="Zip" placeholder="Postcode/Zip" />
      <TextInput label="County" placeholder="Country" />

      <Space h="xl" />
      <Flex justify="center">
        <Button variant="filled" mt="xl" fullWidth>
          Save
        </Button>
      </Flex>
    </Container>
  );
};
const Password = () => {};
const Notifications = () => {};

const data = [
  {
    label: "Account",
    icon: <IconUser stroke={1.5} />,
    element: <Account />,
  },
  {
    label: "Password",
    icon: <IconKey stroke={1.5} />,
    element: <Password />,
  },
  {
    label: "Notifications",
    icon: <IconBell stroke={1.5} />,
    element: <Notifications />,
  },
];

const labels = data.map((item) => {
  return { label: item.label, icon: item.icon };
});

const NavBar = ({ active, setActive }) => {
  return (
    <Navbar height={"100%"} p="xs" width={{ base: 80 }}>
      <Navbar.Section grow>
        {data.map((item, index) => {
          return (
            <Tooltip key={index} label={item.label} position="right">
              <Button
                variant={index === active ? "filled" : "subtle"}
                fullWidth
                onClick={() => {
                  setActive(index);
                }}
              >
                {item.icon}
              </Button>
            </Tooltip>
          );
        })}
      </Navbar.Section>

      <Navbar.Section>
        <Tooltip label="Logout" position="right">
          <Button variant="subtle" fullWidth>
            <IconLogout stroke={1.5} />
          </Button>
        </Tooltip>
      </Navbar.Section>
    </Navbar>
  );
};

const AccountPage = () => {
  const [active, setActive] = useState(0);

  return (
    <AppShell
      navbar={<NavBar active={active} setActive={setActive} />}
      footer={<Footer />}
    >
      <Account />
    </AppShell>
  );
};

export default AccountPage;
