import { SimpleGrid, Container, Title, Group, Select } from "@mantine/core";
import { Carousel } from "@mantine/carousel";
import { IconArrowsSort } from "@tabler/icons-react";

import NavBar from "../components/NavBar";
import ProductCard from "../components/ProductCard";
import img from "../assets/donwload.png";

const MainPage = () => {
  return (
    <>
      <NavBar />
      <Container size="xl">
        <Group
          py={10}
          position="center"
          sx={{
            width: "100%",
            display: "flex",
            flex: 1,
            alignItems: "center",
            justifyContent: "space-between",

            "@media (max-width: 768px)": {
              justifyContent: "center",
            },
          }}
        >
          <Title order={1}>Smartphones</Title>
          <Select
            placeholder="Sort by"
            icon={<IconArrowsSort />}
            data={[
              { label: "Price: Low to High", value: "price-low-to-high" },
              { label: "Price: High to Low", value: "price-high-to-low" },
              { label: "Name: A to Z", value: "name-a-to-z" },
              { label: "Name: Z to A", value: "name-z-to-a" },
            ]}
            sx={{
              "@media (max-width: 768px)": {
                width: "100%",
              },
            }}
          />
        </Group>
        <SimpleGrid
          spacing="lg"
          breakpoints={[
            { minWidth: "sm", cols: 2 },
            { minWidth: "md", cols: 3 },
            { minWidth: "lg", cols: 4 },
          ]}
        >
          <ProductCard img={img} name={"Iphone 14 pro"} price={"999"} />
          <ProductCard img={img} name={"Iphone 14 pro Max"} price={"1099"} />
          <ProductCard img={img} name={"Iphone 14"} price={"54222"} />
          <ProductCard img={img} name={"Iphone 14 Max"} price={"28"} />
          <ProductCard img={img} name={"Iphone 14 Max"} price={"28"} />
        </SimpleGrid>
      </Container>
      <Container size="xl">
        <Title order={1}>Newest</Title>
        <Carousel withIndicators loop dragFree slideGap="lg" slideSize="33.33%">
          <Carousel.Slide>
            <ProductCard img={img} name={"Iphone 14 pro"} price={"999"} />
          </Carousel.Slide>
          <Carousel.Slide>
            <ProductCard img={img} name={"Iphone 14 pro Max"} price={"1099"} />
          </Carousel.Slide>
          <Carousel.Slide>
            <ProductCard img={img} name={"Iphone 14"} price={"54222"} />
          </Carousel.Slide>
          <Carousel.Slide>
            <ProductCard img={img} name={"Iphone 14 Max"} price={"28"} />
          </Carousel.Slide>
        </Carousel>
      </Container>
    </>
  );
};

export default MainPage;
