import {
  TextInput,
  Textarea,
  SimpleGrid,
  Group,
  Title,
  Text,
  Button,
  Container,
  Flex,
} from "@mantine/core";
import { useForm } from "@mantine/form";
import { IconUser, IconAt, IconMessage } from "@tabler/icons-react";

const ContactPage = () => {
  const form = useForm({
    initialValues: {
      name: "",
      email: "",
      subject: "",
      message: "",
    },
    validate: {
      name: (value) =>
        value.length < 2 ? "Name must be at least 2 characters" : null,
      email: (value) => (!/^\S+@\S+$/.test(value) ? "Invalid email" : null),
      subject: (value) =>
        value.length < 2 ? "Subject must be at least 2 characters" : null,
      message: (value) =>
        value.length < 2 ? "Message must be at least 2 characters" : null,
    },
  });

  return (
    <form onSubmit={form.onSubmit(console.log)}>
      <Title order={1} weight={900} align="center">
        Contact Us
      </Title>
      <Text align="center" color="dimmed">
        Leave your email and we will get back to you as soon as possible.
      </Text>
      <SimpleGrid
        columns={1}
        spacing={"md"}
        mt="md"
        breakpoints={[{ minWidth: "sm", cols: 2 }]}
      >
        <TextInput
          label="Name"
          placeholder="Your name"
          icon={<IconUser stroke={1.5} size={"1rem"} />}
          {...form.getInputProps("name")}
        />
        <TextInput
          label="Email"
          placeholder="Your email"
          icon={<IconAt stroke={1.5} size={"1rem"} />}
          {...form.getInputProps("email")}
        />
      </SimpleGrid>
      <TextInput
        label="Subject"
        placeholder="Subject"
        mt="md"
        icon={<IconMessage stroke={1.5} size={"1rem"} />}
        {...form.getInputProps("subject")}
      />
      <Textarea
        label="Message"
        placeholder="Message"
        mt="md"
        {...form.getInputProps("message")}
      />
      <Group position="center" mt="xl">
        <Button type="submit" size="md">
          Send
        </Button>
      </Group>
    </form>
  );
};

export default ContactPage;
