import { Container, Group, Title, Select, SimpleGrid } from "components";
import { IconArrowsSort } from "phosphor-react";
import { useParams } from "react-router-dom";

import ProductCard from "components/ProductCard";

const CategoryPage = () => {
  const { category } = useParams();

  return (
    <Container size="xl">
      <Group
        py={10}
        position="center"
        sx={{
          width: "100%",
          display: "flex",
          flex: 1,
          alignItems: "center",
          justifyContent: "space-between",

          "@media (max-width: 768px)": {
            justifyContent: "center",
          },
        }}
      >
        <Title order={1}>Smartphones</Title>
        <Select
          placeholder="Sort by"
          icon={<IconArrowsSort />}
          data={[
            { label: "Price: Low to High", value: "price-low-to-high" },
            { label: "Price: High to Low", value: "price-high-to-low" },
            { label: "Name: A to Z", value: "name-a-to-z" },
            { label: "Name: Z to A", value: "name-z-to-a" },
          ]}
          sx={{
            "@media (max-width: 768px)": {
              width: "100%",
            },
          }}
        />
      </Group>
      <SimpleGrid
        spacing="lg"
        breakpoints={[
          { minWidth: "sm", cols: 2 },
          { minWidth: "md", cols: 3 },
          { minWidth: "lg", cols: 4 },
        ]}
      >
        <ProductCard img={img} name={"Iphone 14 pro"} price={"999"} />
        <ProductCard img={img} name={"Iphone 14 pro Max"} price={"1099"} />
        <ProductCard img={img} name={"Iphone 14"} price={"54222"} />
        <ProductCard img={img} name={"Iphone 14 Max"} price={"28"} />
        <ProductCard img={img} name={"Iphone 14 Max"} price={"28"} />
      </SimpleGrid>
    </Container>
  );
};

export default CategoryPage;
