import { Navbar, Flex, Divider, Button } from "@mantine/core";
import { IconLogout } from "@tabler/icons-react";
import { Link } from "react-router-dom";

const AccountNavbar = ({ labels, active }) => {
  return (
    <Flex>
      <Navbar height={700} width={{ sm: 300 }} p="md">
        <Navbar.Section>
          {labels.map((item) => {
            return (
              <Button
                key={item.label}
                leftIcon={item.icon}
                variant={item.label === active ? "filled" : "subtle"}
                fullWidth
                onClick={() => {
                  active = item.label;
                }}
              >
                {item.label}
              </Button>
            );
          })}
        </Navbar.Section>
        <Divider />
        <Navbar.Section
          style={{
            bottom: 0,
          }}
        >
          <Button
            variant="subtle"
            leftIcon={<IconLogout stroke={1.5} />}
            fullWidth
          >
            Logout
          </Button>
        </Navbar.Section>
      </Navbar>
    </Flex>
  );
};

export default AccountNavbar;
